import Layout from '../components/Layout';
import fetch from 'isomorphic-unfetch';

const Project = props => (
  <Layout>
    <h1>{props.project.name}</h1>
    <p>{props.project.description.replace(/<[/]?[pb]>/g, '')}</p>
    <img src={props.project.technologies} />
  </Layout>
);

Post.getInitialProps = async function(context) {
  const { id } = context.query;
  const res = await fetch(`https://my-json-server.typicode.com/milcahhalili/projects.json/projects`);
  const project = await res.json();

  console.log(`Fetched project: ${project.name}`);

  return { project };
};

export default Project;
