import React, { Component } from 'react';
import Link from 'next/link';

export default class Projects extends Component {
  constructor(props) {
    super(props);
    this.state = {
      hover: false
    };
  }

  toggleHover = () => {
    this.setState(function(prevState) {
      return {
        hover: !prevState.show
      }
    });
  }

  render() {
    const { project } = this.props;
    return (
      <>
        <div className="container">
            <div className="projects-cntnr">
              <Link href="/p[id]" as={`/p${project.endpoint}`}>
                <a>{project.name}</a>
              </Link>
            </div>
        </div>
        <style jsx>{`
          div {
            display: flex;
            align-items: center;
          }
          .container {
            align-items: center;
            justify-content: center;
            width: 45%;
            height: 200px;
            text-align: center;
            background-color: lightblue;
            margin: 10px 10px;
          }
          img {
            height: 200px;
          }
          a {
            text-transform: uppercase;
          }
          @media screen and (max-width: 420px) {
            .container {
              width: 100%;
            }
          }
        `}</style>
      </>
    );
  }
}
